export const MemberShipFeature = ({ data, textColor }) => {
    return (
        <>
            <div className="flex items-center gap-3 mb-3">
                <span>
                    {
                        data.is_active ?
                            <img src={`${process.env.PUBLIC_URL}/images/Done.png`} />
                            :
                            <img src={`${process.env.PUBLIC_URL}/images/Close.png`} />
                    }
                </span>
                <p className={`${textColor}`}>
                    {
                        data.title
                    }
                </p>
            </div>
        </>
    )
}
MemberShipFeature.defaultProps = {
    textColor: 'text-[#7A7A7A]'
}


const MemberShipCard = ({ data, ...props }) => {
    return (
        <>
            <div
                className={`flex-1 relative max-w-[400px] mx-auto sm:min-w-[300px] w-full rounded-[15px] shadow-gray-800 p-3 bg-white ${data.is_featured ? 'lg:scale-110 shadow-xl mt-10 sm:mt-0 pt-10 border-2 border-red-600' : 'pt-4 lg:hover:scale-105 hover:shadow-lg'}  transition-all cursor-pointer `}
            >
                {
                    data.is_featured &&
                    <img
                        src={`${process.env.PUBLIC_URL}/images/thunmbsup.png`}
                        className={`absolute top-0 left-[30px] w-[120px] -translate-y-1/2`}
                        alt="" />
                }
                <div className="px-4">
                    <h3 className={`text-center text-[22px] mb-2 ${data.is_featured && 'italic'}`}>{data.name}</h3>
                    <p className="text-center">$<span className="text-red-600 text-[24px]">{data.price}</span>/<span className='text-gray-600 italic'>{data.duration == 'MONTHLY' ? 'month' : 'year'}</span></p>
                    <hr className="my-4" />
                </div>
                <div className="px-4">
                    {
                        data.features.map((ft, index) => {
                            return (
                                <>
                                    <MemberShipFeature data={ft} key={index} />
                                </>
                            )
                        })
                    }
                </div>
                <div className={`w-max mx-auto border transition-all px-3 py-1.5 rounded-md mt-4 ${data.is_featured ? 'bg-red-600 text-white border-red-600' : 'text-gray-800 border-red-600 hover:bg-red-600 hover:text-white'}`}>
                    <p>Subscribe Now</p>
                </div>
            </div>
        </>
    )
}

export default MemberShipCard